option.value:
package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.OfflinePlayer;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import java.util.Date;
import java.util.Set;
import org.bukkit.configuration.ConfigurationSection;

public class BanCommand implements CommandExecutor {

    private final BanPlugin plugin;

    public BanCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        // Check permission
        if (!sender.hasPermission("banplugin.ban")) {
            sender.sendMessage(colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        // Check arguments
        if (args.length == 0) {
            showBanUsage(sender);
            return true;
        }

        if (args.length < 2) {
            sender.sendMessage(colorize(plugin.getConfig().getString("messages.usage-ban")));
            return true;
        }

        String playerName = args[0];
        boolean useIdSystem = plugin.getConfig().getBoolean("settings.use-id-system.ban", true);

        // Combine all arguments after player name for the reason/id
        StringBuilder inputBuilder = new StringBuilder();
        for (int i = 1; i < args.length; i++) {
            inputBuilder.append(args[i]);
            if (i < args.length - 1) {
                inputBuilder.append(" ");
            }
        }
        String input = inputBuilder.toString();

        // Find the ban entry either by ID or reason
        String banId = null;
        ConfigurationSection bans = plugin.getConfig().getConfigurationSection("bans");

        if (useIdSystem) {
            // Using ID system
            if (!bans.contains(input)) {
                Set<String> availableIds = bans.getKeys(false);
                String idsString = String.join(", ", availableIds);
                String message = plugin.getConfig().getString("messages.invalid-ban-id")
                        .replace("{ids}", idsString);
                sender.sendMessage(colorize(message));
                return true;
            }
            banId = input;
        } else {
            // Using reason system - find the ID by reason
            for (String id : bans.getKeys(false)) {
                String reason = bans.getString(id + ".reason");
                if (reason != null && reason.equalsIgnoreCase(input)) {
                    banId = id;
                    break;
                }
            }

            if (banId == null) {
                sender.sendMessage(colorize("&cInvalid ban reason! Use TAB to see available reasons."));
                return true;
            }
        }

        // Get player (online or offline) - allows pre-emptive bans
        OfflinePlayer target = Bukkit.getOfflinePlayer(playerName);

        // Check if target has bypass permission (prevent banning staff)
        if (target.isOnline()) {
            Player onlineTarget = (Player) target;
            if (onlineTarget.hasPermission("banplugin.bypass")) {
                sender.sendMessage(colorize(plugin.getConfig().getString("messages.cannot-ban-staff")));
                return true;
            }
        }

        // Check if player is already banned
        if (plugin.getBansConfig().contains("banned-players." + target.getUniqueId().toString())) {
            String message = plugin.getConfig().getString("messages.already-banned")
                    .replace("{player}", target.getName());
            sender.sendMessage(colorize(message));
            return true;
        }

        // Get ban details
        ConfigurationSection banSection = plugin.getConfig().getConfigurationSection("bans." + banId);
        String reason = banSection.getString("reason");
        String durationStr = banSection.getString("duration");
        long duration = parseDuration(durationStr);
        // Calculate expiry time (-1 duration means permanent ban)
        long expiryTime = duration == -1 ? -1 : System.currentTimeMillis() + duration;

        String banner = sender instanceof org.bukkit.command.ConsoleCommandSender ? "CONSOLE" : sender.getName();

        // Add to history
        plugin.addToHistory(
            target.getUniqueId().toString(),
            "BAN",
            banner,
            reason,
            duration
        );

        // Save ban in config
        String banPath = "banned-players." + target.getUniqueId().toString();
        plugin.getBansConfig().set(banPath + ".reason", reason);
        plugin.getBansConfig().set(banPath + ".by", banner);
        plugin.getBansConfig().set(banPath + ".time", System.currentTimeMillis());
        plugin.getBansConfig().set(banPath + ".duration", duration);
        plugin.saveBansConfig();

        // Kick player if online
        if (target.isOnline()) {
            Player onlineTarget = (Player) target;
            String kickMessage = plugin.getConfig().getString("messages.player-banned")
                    .replace("{reason}", reason)
                    .replace("{expires}", new Date(expiryTime).toString());
            onlineTarget.kickPlayer(colorize(kickMessage));
        }

        // Send success message
        String formattedDuration = duration == -1 ? "Permanent" : formatDuration(duration);
        String successMessage = plugin.getConfig().getString("messages.ban-success")
                .replace("{player}", target.getName())
                .replace("{reason}", reason)
                .replace("{duration}", formattedDuration);
        sender.sendMessage(colorize(successMessage));

        return true;
    }

    private void showBanUsage(CommandSender sender) {
        String divider = plugin.getConfig().getString("usage-format.divider", "&7&m--------------------------------");
        boolean useIdSystem = plugin.getConfig().getBoolean("settings.use-id-system.ban", true);

        String header = useIdSystem ?
            plugin.getConfig().getString("usage-format.ban-command.header", "&cUsage: &7/ban <player> <ban-id>") :
            "&cUsage: &7/ban <player> <reason>";

        String listHeader = useIdSystem ?
            plugin.getConfig().getString("usage-format.ban-command.list-header", "&cAvailable Ban IDs:") :
            "&cAvailable Ban Reasons:";

        String format = useIdSystem ?
            plugin.getConfig().getString("usage-format.ban-command.format", "&7ID: &c{id} &7| Reason: &c{reason} &7| Duration: &c{duration}") :
            "&7Reason: &c{reason} &7| Duration: &c{duration}";

        sender.sendMessage(colorize(divider));
        sender.sendMessage(colorize(header));
        sender.sendMessage(colorize(divider));
        sender.sendMessage(colorize(listHeader));

        if (plugin.getConfig().getConfigurationSection("bans") != null) {
            for (String id : plugin.getConfig().getConfigurationSection("bans").getKeys(false)) {
                String reason = plugin.getConfig().getString("bans." + id + ".reason");
                String duration = plugin.getConfig().getString("bans." + id + ".duration");
                String line = format
                    .replace("{id}", id)
                    .replace("{reason}", reason)
                    .replace("{duration}", duration);
                sender.sendMessage(colorize(line));
            }
        }

        sender.sendMessage(colorize(divider));
    }

    private long parseDuration(String duration) {
        if (duration == null || duration.isEmpty()) {
            return 24 * 60 * 60 * 1000; // Default 1 day
        }

        String timeUnit = duration.substring(duration.length() - 1).toLowerCase();
        String timeValue = duration.substring(0, duration.length() - 1);

        try {
            long value = Long.parseLong(timeValue);
            switch (timeUnit) {
                case "s":
                    return value * 1000;
                case "m":
                    return value * 60 * 1000;
                case "h":
                    return value * 60 * 60 * 1000;
                case "d":
                    return value * 24 * 60 * 60 * 1000;
                default:
                    return 24 * 60 * 60 * 1000; // Default 1 day
            }
        } catch (NumberFormatException e) {
            return 24 * 60 * 60 * 1000; // Default 1 day
        }
    }

    private String formatDuration(long duration) {
        long seconds = duration / 1000;
        long minutes = seconds / 60;
        long hours = minutes / 60;
        long days = hours / 24;

        if (days > 0) {
            return days + " day(s)";
        } else if (hours > 0) {
            return hours + " hour(s)";
        } else if (minutes > 0) {
            return minutes + " minute(s)";
        } else {
            return seconds + " second(s)";
        }
    }

    private String colorize(String message) {
        return ChatColor.translateAlternateColorCodes('&', message);
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.OfflinePlayer;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import java.util.Date;
import java.util.Set;
import org.bukkit.configuration.ConfigurationSection;

public class BanCommand implements CommandExecutor {

    private final BanPlugin plugin;

    public BanCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        // Check permission
        if (!sender.hasPermission("banplugin.ban")) {
            sender.sendMessage(colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        // Check arguments
        if (args.length == 0) {
            showBanUsage(sender);
            return true;
        }

        if (args.length < 2) {
            sender.sendMessage(colorize(plugin.getConfig().getString("messages.usage-ban")));
            return true;
        }

        String playerName = args[0];
        boolean useIdSystem = plugin.getConfig().getBoolean("settings.use-id-system.ban", true);

        // Combine all arguments after player name for the reason/id
        StringBuilder inputBuilder = new StringBuilder();
        for (int i = 1; i < args.length; i++) {
            inputBuilder.append(args[i]);
            if (i < args.length - 1) {
                inputBuilder.append(" ");
            }
        }
        String input = inputBuilder.toString();

        // Find the ban entry either by ID or reason
        String banId = null;
        ConfigurationSection bans = plugin.getConfig().getConfigurationSection("bans");

        if (useIdSystem) {
            // Using ID system
            if (!bans.contains(input)) {
                Set<String> availableIds = bans.getKeys(false);
                String idsString = String.join(", ", availableIds);
                String message = plugin.getConfig().getString("messages.invalid-ban-id")
                        .replace("{ids}", idsString);
                sender.sendMessage(colorize(message));
                return true;
            }
            banId = input;
        } else {
            // Using reason system - find the ID by reason
            for (String id : bans.getKeys(false)) {
                String reason = bans.getString(id + ".reason");
                if (reason != null && reason.equalsIgnoreCase(input)) {
                    banId = id;
                    break;
                }
            }

            if (banId == null) {
                sender.sendMessage(colorize("&cInvalid ban reason! Use TAB to see available reasons."));
                return true;
            }
        }

        // Get player (online or offline) - allows pre-emptive bans
        OfflinePlayer target = Bukkit.getOfflinePlayer(playerName);

        // Check if target has bypass permission (prevent banning staff)
        if (target.isOnline()) {
            Player onlineTarget = (Player) target;
            if (onlineTarget.hasPermission("banplugin.bypass") || onlineTarget.isOp()) {
                sender.sendMessage(colorize(plugin.getConfig().getString("messages.staff-bypass")));
                return true;
            }
        }

        // Check if player is already banned
        if (plugin.getBansConfig().contains("banned-players." + target.getUniqueId().toString())) {
            String message = plugin.getConfig().getString("messages.already-banned")
                    .replace("{player}", target.getName());
            sender.sendMessage(colorize(message));
            return true;
        }

        // Get ban details
        ConfigurationSection banSection = plugin.getConfig().getConfigurationSection("bans." + banId);
        String reason = banSection.getString("reason");
        String durationStr = banSection.getString("duration");
        long duration = parseDuration(durationStr);
        // Calculate expiry time (-1 duration means permanent ban)
        long expiryTime = duration == -1 ? -1 : System.currentTimeMillis() + duration;

        String banner = sender instanceof org.bukkit.command.ConsoleCommandSender ? "CONSOLE" : sender.getName();

        // Add to history
        plugin.addToHistory(
            target.getUniqueId().toString(),
            "BAN",
            banner,
            reason,
            duration
        );

        // Save ban in config
        String banPath = "banned-players." + target.getUniqueId().toString();
        plugin.getBansConfig().set(banPath + ".reason", reason);
        plugin.getBansConfig().set(banPath + ".by", banner);
        plugin.getBansConfig().set(banPath + ".time", System.currentTimeMillis());
        plugin.getBansConfig().set(banPath + ".duration", duration);
        plugin.saveBansConfig();

        // Kick player if online
        if (target.isOnline()) {
            Player onlineTarget = (Player) target;
            String kickMessage = plugin.getConfig().getString("messages.player-banned")
                    .replace("{reason}", reason)
                    .replace("{expires}", new Date(expiryTime).toString());
            onlineTarget.kickPlayer(colorize(kickMessage));
        }

        // Send success message
        String formattedDuration = duration == -1 ? "Permanent" : formatDuration(duration);
        String successMessage = plugin.getConfig().getString("messages.ban-success")
                .replace("{player}", target.getName())
                .replace("{reason}", reason)
                .replace("{duration}", formattedDuration);
        sender.sendMessage(colorize(successMessage));

        return true;
    }

    private void showBanUsage(CommandSender sender) {
        String divider = plugin.getConfig().getString("usage-format.divider", "&7&m--------------------------------");
        boolean useIdSystem = plugin.getConfig().getBoolean("settings.use-id-system.ban", true);

        String header = useIdSystem ?
            plugin.getConfig().getString("usage-format.ban-command.header", "&cUsage: &7/ban <player> <ban-id>") :
            "&cUsage: &7/ban <player> <reason>";

        String listHeader = useIdSystem ?
            plugin.getConfig().getString("usage-format.ban-command.list-header", "&cAvailable Ban IDs:") :
            "&cAvailable Ban Reasons:";

        String format = useIdSystem ?
            plugin.getConfig().getString("usage-format.ban-command.format", "&7ID: &c{id} &7| Reason: &c{reason} &7| Duration: &c{duration}") :
            "&7Reason: &c{reason} &7| Duration: &c{duration}";

        sender.sendMessage(colorize(divider));
        sender.sendMessage(colorize(header));
        sender.sendMessage(colorize(divider));
        sender.sendMessage(colorize(listHeader));

        if (plugin.getConfig().getConfigurationSection("bans") != null) {
            for (String id : plugin.getConfig().getConfigurationSection("bans").getKeys(false)) {
                String reason = plugin.getConfig().getString("bans." + id + ".reason");
                String duration = plugin.getConfig().getString("bans." + id + ".duration");
                String line = format
                    .replace("{id}", id)
                    .replace("{reason}", reason)
                    .replace("{duration}", duration);
                sender.sendMessage(colorize(line));
            }
        }

        sender.sendMessage(colorize(divider));
    }

    private long parseDuration(String duration) {
        if (duration == null || duration.isEmpty()) {
            return -1; // Permanent ban by default
        }

        try {
            // Check if it's a special value
            if (duration.equalsIgnoreCase("permanent") || duration.equals("-1")) {
                return -1;
            }

            String timeUnit = duration.substring(duration.length() - 1).toLowerCase();
            String timeValue = duration.substring(0, duration.length() - 1);

            long value = Long.parseLong(timeValue);
            switch (timeUnit) {
                case "s":
                    return value * 1000;
                case "m":
                    return value * 60 * 1000;
                case "h":
                    return value * 60 * 60 * 1000;
                case "d":
                    return value * 24 * 60 * 60 * 1000;
                default:
                    plugin.getLogger().warning("Invalid duration format in config: " + duration + ". Using permanent ban.");
                    return -1;
            }
        } catch (NumberFormatException | StringIndexOutOfBoundsException e) {
            plugin.getLogger().warning("Invalid duration format in config: " + duration + ". Using permanent ban.");
            return -1;
        }
    }

    private String formatDuration(long duration) {
        long seconds = duration / 1000;
        long minutes = seconds / 60;
        long hours = minutes / 60;
        long days = hours / 24;

        if (days > 0) {
            return days + " day(s)";
        } else if (hours > 0) {
            return hours + " hour(s)";
        } else if (minutes > 0) {
            return minutes + " minute(s)";
        } else {
            return seconds + " second(s)";
        }
    }

    private String colorize(String message) {
        return ChatColor.translateAlternateColorCodes('&', message);
    }
}

package org.viirless.smartban;

import org.bukkit.plugin.java.JavaPlugin;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.configuration.file.YamlConfiguration;
import java.io.File;
import java.io.IOException;
import java.util.HashSet;
import java.util.Set;
import java.util.UUID;

public class BanPlugin extends JavaPlugin {

    private File bansFile;
    private FileConfiguration bansConfig;
    private File historyFile;
    private FileConfiguration historyConfig;
    private Set<UUID> frozenPlayers;
    private Set<UUID> vanishedPlayers; // Add this field

    @Override
    public void onEnable() {
        // Initialize sets
        frozenPlayers = new HashSet<>();
        vanishedPlayers = new HashSet<>(); // Initialize vanished players set

        // Save default config if it doesn't exist
        saveDefaultConfig();

        // Create bans.yml and history.yml files
        createBansFile();
        createHistoryFile();

        // Create TabCompleter instance
        BanTabCompleter tabCompleter = new BanTabCompleter(this);

        // Register commands and tab completers
        getCommand("ban").setExecutor(new BanCommand(this));
        getCommand("unban").setExecutor(new UnbanCommand(this));
        getCommand("kick").setExecutor(new KickCommand(this));
        getCommand("mute").setExecutor(new MuteCommand(this));
        getCommand("unmute").setExecutor(new UnmuteCommand(this));
        getCommand("history").setExecutor(new HistoryCommand(this));
        getCommand("smartbans").setExecutor(new ReloadCommand(this));
        getCommand("freeze").setExecutor(new FreezeCommand(this));
        getCommand("unfreeze").setExecutor(new UnfreezeCommand(this));
        getCommand("examine").setExecutor(new ExamineCommand(this));
        getCommand("vanish").setExecutor(new VanishCommand(this));
        getCommand("invsee").setExecutor(new InvseeCommand(this));

        // Register tab completers
        getCommand("ban").setTabCompleter(tabCompleter);
        getCommand("mute").setTabCompleter(tabCompleter);

        // Register event listeners
        getServer().getPluginManager().registerEvents(new PlayerJoinListener(this), this);
        getServer().getPluginManager().registerEvents(new ChatListener(this), this);
        getServer().getPluginManager().registerEvents(new PlayerFreezeListener(this), this);
        getServer().getPluginManager().registerEvents(new ExamineListener(this), this);

        getLogger().info("Ban Plugin has been enabled!");
    }

    @Override
    public void onDisable() {
        getLogger().info("Ban Plugin has been disabled!");
    }

    private void createBansFile() {
        bansFile = new File(getDataFolder(), "bans.yml");
        if (!bansFile.exists()) {
            bansFile.getParentFile().mkdirs();
            try {
                bansFile.createNewFile();
            } catch (IOException e) {
                getLogger().severe("Could not create bans.yml file!");
                e.printStackTrace();
            }
        }
        bansConfig = YamlConfiguration.loadConfiguration(bansFile);
    }

    private void createHistoryFile() {
        historyFile = new File(getDataFolder(), "history.yml");
        if (!historyFile.exists()) {
            saveResource("history.yml", false);
        }
        historyConfig = YamlConfiguration.loadConfiguration(historyFile);
    }

    public FileConfiguration getBansConfig() {
        return bansConfig;
    }

    public void saveBansConfig() {
        try {
            bansConfig.save(bansFile);
        } catch (IOException e) {
            getLogger().severe("Could not save bans.yml!");
            e.printStackTrace();
        }
    }

    public void reloadBansConfig() {
        bansConfig = YamlConfiguration.loadConfiguration(bansFile);
    }

    public FileConfiguration getHistoryConfig() {
        return historyConfig;
    }

    public void saveHistoryConfig() {
        try {
            historyConfig.save(historyFile);
        } catch (IOException e) {
            getLogger().severe("Could not save history.yml!");
            e.printStackTrace();
        }
    }

    public void reloadHistoryConfig() {
        if (historyFile == null) {
            historyFile = new File(getDataFolder(), "history.yml");
        }
        historyConfig = YamlConfiguration.loadConfiguration(historyFile);
    }

    public void addToHistory(String uuid, String type, String by, String reason, long duration) {
        String key = uuid + "." + System.currentTimeMillis();
        historyConfig.set(key + ".type", type);
        historyConfig.set(key + ".by", by);
        historyConfig.set(key + ".reason", reason);
        historyConfig.set(key + ".date", System.currentTimeMillis());
        historyConfig.set(key + ".duration", duration);
        saveHistoryConfig();
    }

    public Set<UUID> getFrozenPlayers() {
        return frozenPlayers;
    }

    public Set<UUID> getVanishedPlayers() {
        return vanishedPlayers;
    }

    public String colorize(String message) {
        return message != null ? org.bukkit.ChatColor.translateAlternateColorCodes('&', message) : "";
    }
}

package org.viirless.smartban;

import org.bukkit.plugin.java.JavaPlugin;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.configuration.file.YamlConfiguration;
import java.io.File;
import java.io.IOException;
import java.util.HashSet;
import java.util.Set;
import java.util.UUID;

public class BanPlugin extends JavaPlugin {

    private File bansFile;
    private FileConfiguration bansConfig;
    private File historyFile;
    private FileConfiguration historyConfig;
    private Set<UUID> frozenPlayers;
    private Set<UUID> vanishedPlayers; // Add this field

    @Override
    public void onEnable() {
        // Initialize sets
        frozenPlayers = new HashSet<>();
        vanishedPlayers = new HashSet<>(); // Initialize vanished players set

        // Save default config if it doesn't exist
        saveDefaultConfig();

        // Create bans.yml and history.yml files
        createBansFile();
        createHistoryFile();

        // Create TabCompleter instance
        BanTabCompleter tabCompleter = new BanTabCompleter(this);

        // Register commands and tab completers
        getCommand("ban").setExecutor(new BanCommand(this));
        getCommand("unban").setExecutor(new UnbanCommand(this));
        getCommand("kick").setExecutor(new KickCommand(this));
        getCommand("mute").setExecutor(new MuteCommand(this));
        getCommand("unmute").setExecutor(new UnmuteCommand(this));
        getCommand("history").setExecutor(new HistoryCommand(this));
        getCommand("smartbans").setExecutor(new ReloadCommand(this));
        getCommand("freeze").setExecutor(new FreezeCommand(this));
        getCommand("unfreeze").setExecutor(new UnfreezeCommand(this));
        getCommand("examine").setExecutor(new ExamineCommand(this));
        getCommand("vanish").setExecutor(new VanishCommand(this));
        getCommand("invsee").setExecutor(new InvseeCommand(this));
        getCommand("clearinv").setExecutor(new ClearInventoryCommand(this));

        // Register tab completers
        getCommand("ban").setTabCompleter(tabCompleter);
        getCommand("mute").setTabCompleter(tabCompleter);

        // Register event listeners
        getServer().getPluginManager().registerEvents(new PlayerJoinListener(this), this);
        getServer().getPluginManager().registerEvents(new ChatListener(this), this);
        getServer().getPluginManager().registerEvents(new PlayerFreezeListener(this), this);
        getServer().getPluginManager().registerEvents(new ExamineListener(this), this);

        getLogger().info("Ban Plugin has been enabled!");
    }

    @Override
    public void onDisable() {
        getLogger().info("Ban Plugin has been disabled!");
    }

    private void createBansFile() {
        bansFile = new File(getDataFolder(), "bans.yml");
        if (!bansFile.exists()) {
            bansFile.getParentFile().mkdirs();
            try {
                bansFile.createNewFile();
            } catch (IOException e) {
                getLogger().severe("Could not create bans.yml file!");
                e.printStackTrace();
            }
        }
        bansConfig = YamlConfiguration.loadConfiguration(bansFile);
    }

    private void createHistoryFile() {
        historyFile = new File(getDataFolder(), "history.yml");
        if (!historyFile.exists()) {
            saveResource("history.yml", false);
        }
        historyConfig = YamlConfiguration.loadConfiguration(historyFile);
    }

    public FileConfiguration getBansConfig() {
        return bansConfig;
    }

    public void saveBansConfig() {
        try {
            bansConfig.save(bansFile);
        } catch (IOException e) {
            getLogger().severe("Could not save bans.yml!");
            e.printStackTrace();
        }
    }

    public void reloadBansConfig() {
        bansConfig = YamlConfiguration.loadConfiguration(bansFile);
    }

    public FileConfiguration getHistoryConfig() {
        return historyConfig;
    }

    public void saveHistoryConfig() {
        try {
            historyConfig.save(historyFile);
        } catch (IOException e) {
            getLogger().severe("Could not save history.yml!");
            e.printStackTrace();
        }
    }

    public void reloadHistoryConfig() {
        if (historyFile == null) {
            historyFile = new File(getDataFolder(), "history.yml");
        }
        historyConfig = YamlConfiguration.loadConfiguration(historyFile);
    }

    public void addToHistory(String uuid, String type, String by, String reason, long duration) {
        String key = uuid + "." + System.currentTimeMillis();
        historyConfig.set(key + ".type", type);
        historyConfig.set(key + ".by", by);
        historyConfig.set(key + ".reason", reason);
        historyConfig.set(key + ".date", System.currentTimeMillis());
        historyConfig.set(key + ".duration", duration);
        saveHistoryConfig();
    }

    public Set<UUID> getFrozenPlayers() {
        return frozenPlayers;
    }

    public Set<UUID> getVanishedPlayers() {
        return vanishedPlayers;
    }

    public String colorize(String message) {
        return message != null ? org.bukkit.ChatColor.translateAlternateColorCodes('&', message) : "";
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class ClearInventoryCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public ClearInventoryCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("banplugin.clearinv")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        // If no args, clear own inventory (if sender is player)
        if (args.length == 0) {
            if (!(sender instanceof Player)) {
                sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.clearinv.usage")));
                return true;
            }
            Player player = (Player) sender;
            clearInventory(player);
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.clearinv.cleared-self")));
            return true;
        }

        // Clear target player's inventory
        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Prevent clearing inventory of staff members
        if (target.hasPermission("banplugin.clearinv.bypass")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        clearInventory(target);
        sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.clearinv.cleared-other")
                .replace("{player}", target.getName())));
        target.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.clearinv.cleared-by")
                .replace("{staff}", sender.getName())));

        return true;
    }

    private void clearInventory(Player player) {
        player.getInventory().clear();
        player.getInventory().setArmorContents(null);
        player.getInventory().setItemInOffHand(null);
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.Material;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.inventory.meta.SkullMeta;

import java.util.ArrayList;
import java.util.List;

public class ExamineCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public ExamineCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(plugin.colorize("&cThis command can only be used by players!"));
            return true;
        }

        if (!sender.hasPermission("banplugin.examine")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length != 1) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.examine.usage")));
            return true;
        }

        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.examine.offline")));
            return true;
        }

        openExamineGUI((Player) sender, target);
        return true;
    }

    private void openExamineGUI(Player staff, Player target) {
        Inventory gui = Bukkit.createInventory(null, 27, plugin.colorize(
                plugin.getConfig().getString("messages.examine.title")
                        .replace("{player}", target.getName())
        ));

        // Spielerkopf in der Mitte
        ItemStack head = new ItemStack(Material.PLAYER_HEAD);
        SkullMeta skullMeta = (SkullMeta) head.getItemMeta();
        skullMeta.setOwningPlayer(target);
        skullMeta.setDisplayName(plugin.colorize("&e" + target.getName()));
        List<String> headLore = new ArrayList<>();
        headLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.name")
                .replace("{name}", target.getName())));
        headLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.uuid")
                .replace("{uuid}", target.getUniqueId().toString())));
        skullMeta.setLore(headLore);
        head.setItemMeta(skullMeta);
        gui.setItem(13, head);

        // Spieler-Infos (Paper mit Details)
        ItemStack info = new ItemStack(Material.PAPER);
        ItemMeta infoMeta = info.getItemMeta();
        infoMeta.setDisplayName(plugin.colorize("&ePlayer Information"));
        List<String> infoLore = new ArrayList<>();
        infoLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.ip")
                .replace("{ip}", target.getAddress().getAddress().getHostAddress())));
        infoLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.location")
                .replace("{world}", target.getWorld().getName())
                .replace("{x}", String.format("%.2f", target.getLocation().getX()))
                .replace("{y}", String.format("%.2f", target.getLocation().getY()))
                .replace("{z}", String.format("%.2f", target.getLocation().getZ()))));
        infoLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.gamemode")
                .replace("{gamemode}", target.getGameMode().toString())));
        infoMeta.setLore(infoLore);
        info.setItemMeta(infoMeta);
        gui.setItem(11, info);

        // Status-Infos (Kompass)
        ItemStack status = new ItemStack(Material.COMPASS);
        ItemMeta statusMeta = status.getItemMeta();
        statusMeta.setDisplayName(plugin.colorize("&ePlayer Status"));
        List<String> statusLore = new ArrayList<>();
        statusLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.health")
                .replace("{health}", String.format("%.1f", target.getHealth()))));
        statusLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.food")
                .replace("{food}", String.valueOf(target.getFoodLevel()))));
        statusLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.exp")
                .replace("{exp}", String.valueOf(target.getLevel()))));
        statusLore.add(plugin.colorize(plugin.getConfig().getString("messages.examine.info.op")
                .replace("{op}", target.isOp() ? "Yes" : "No")));
        statusMeta.setLore(statusLore);
        status.setItemMeta(statusMeta);
        gui.setItem(15, status);

        // History-Button (Redstone)
        ItemStack history = new ItemStack(Material.REDSTONE);
        ItemMeta historyMeta = history.getItemMeta();
        historyMeta.setDisplayName(plugin.colorize("&cView History"));
        List<String> historyLore = new ArrayList<>();
        historyLore.add(plugin.colorize("&7Click to view"));
        historyLore.add(plugin.colorize("&7punishment history"));
        historyMeta.setLore(historyLore);
        history.setItemMeta(historyMeta);
        gui.setItem(26, history);

        staff.openInventory(gui);
    }
}

