package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class FreezeCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public FreezeCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("banplugin.freeze")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length < 1) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.freeze-usage")));
            return true;
        }

        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Verhindere, dass Spieler sich selbst freezen knnen
        if (sender instanceof Player && target.getName().equals(((Player) sender).getName())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        if (plugin.getFrozenPlayers().contains(target.getUniqueId())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.freeze.already-frozen")));
            return true;
        }

        // berprfe ob der Zielspieler OP oder die bypass Permission hat
        if (target.isOp() || target.hasPermission("banplugin.freeze.bypass")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        plugin.getFrozenPlayers().add(target.getUniqueId());
        String message = plugin.getConfig().getString("messages.freeze.frozen-by")
                .replace("{player}", target.getName());
        sender.sendMessage(plugin.colorize(message));

        String mainTitle = plugin.getConfig().getString("messages.freeze.title.main");
        String subtitle = plugin.getConfig().getString("messages.freeze.title.subtitle");

        target.sendTitle(
                plugin.colorize(mainTitle),
                plugin.colorize(subtitle),
                10,
                Integer.MAX_VALUE,
                20
        );

        return true;
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class FreezeCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public FreezeCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("banplugin.freeze")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length < 1) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.freeze-usage")));
            return true;
        }

        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Prevent players from freezing themselves
        if (sender instanceof Player && target.getName().equals(((Player) sender).getName())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        if (plugin.getFrozenPlayers().contains(target.getUniqueId())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.freeze.already-frozen")));
            return true;
        }

        // Check if the target player is OP or has the bypass permission
        if (target.isOp() || target.hasPermission("banplugin.freeze.bypass")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        plugin.getFrozenPlayers().add(target.getUniqueId());
        String message = plugin.getConfig().getString("messages.freeze.frozen-by")
                .replace("{player}", target.getName());
        sender.sendMessage(plugin.colorize(message));

        String mainTitle = plugin.getConfig().getString("messages.freeze.title.main");
        String subtitle = plugin.getConfig().getString("messages.freeze.title.subtitle");

        target.sendTitle(
                plugin.colorize(mainTitle),
                plugin.colorize(subtitle),
                10,
                Integer.MAX_VALUE,
                20
        );

        return true;
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class InvseeCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public InvseeCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(plugin.colorize("&cThis command can only be used by players!"));
            return true;
        }

        if (!sender.hasPermission("banplugin.invsee")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length != 1) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.invsee.usage")));
            return true;
        }

        Player staff = (Player) sender;
        Player target = Bukkit.getPlayer(args[0]);

        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Prevent players from viewing their own inventory
        if (target.equals(staff)) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        // Prevent viewing inventory of staff members
        if (target.hasPermission("banplugin.invsee.bypass")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        staff.openInventory(target.getInventory());
        staff.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.invsee.opened")
                .replace("{player}", target.getName())));

        return true;
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.event.inventory.InventoryDragEvent;
import org.bukkit.inventory.Inventory;

public class InvseeCommand implements CommandExecutor, Listener {
    private final BanPlugin plugin;

    public InvseeCommand(BanPlugin plugin) {
        this.plugin = plugin;
        // Register this class as event listener
        plugin.getServer().getPluginManager().registerEvents(this, plugin);
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(plugin.colorize("&cThis command can only be used by players!"));
            return true;
        }

        if (!sender.hasPermission("banplugin.invsee")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length != 1) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.invsee.usage")));
            return true;
        }

        Player staff = (Player) sender;
        Player target = Bukkit.getPlayer(args[0]);

        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Prevent players from viewing their own inventory
        if (target.equals(staff)) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        // Prevent viewing inventory of staff members
        if (target.hasPermission("banplugin.invsee.bypass")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        // Open inventory and send message
        Inventory inv = target.getInventory();
        staff.openInventory(inv);

        if (!staff.hasPermission("banplugin.invsee.modify")) {
            staff.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.invsee.view-only")));
        }

        staff.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.invsee.opened")
                .replace("{player}", target.getName())));

        return true;
    }

    @EventHandler
    public void onInventoryClick(InventoryClickEvent event) {
        if (!(event.getWhoClicked() instanceof Player)) return;

        // Check if this is an invsee inventory
        if (event.getInventory().equals(event.getWhoClicked().getOpenInventory().getTopInventory()) &&
                event.getInventory().getHolder() instanceof Player &&
                !event.getWhoClicked().equals(event.getInventory().getHolder())) {

            // Cancel if player doesn't have modify permission
            if (!event.getWhoClicked().hasPermission("banplugin.invsee.modify")) {
                event.setCancelled(true);
            }
        }
    }

    @EventHandler
    public void onInventoryDrag(InventoryDragEvent event) {
        if (!(event.getWhoClicked() instanceof Player)) return;

        // Check if this is an invsee inventory
        if (event.getInventory().equals(event.getWhoClicked().getOpenInventory().getTopInventory()) &&
                event.getInventory().getHolder() instanceof Player &&
                !event.getWhoClicked().equals(event.getInventory().getHolder())) {

            // Cancel if player doesn't have modify permission
            if (!event.getWhoClicked().hasPermission("banplugin.invsee.modify")) {
                event.setCancelled(true);
            }
        }
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class KickCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public KickCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("banplugin.kick")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length < 2) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.usage-kick")));
            return true;
        }

        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Verhindere, dass Spieler sich selbst kicken knnen
        if (sender instanceof Player && target.getName().equals(((Player) sender).getName())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        // Staff-Bypass Check
        if (target.hasPermission("banplugin.bypass") || target.isOp()) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        // Combine remaining arguments for kick reason
        StringBuilder reason = new StringBuilder();
        for (int i = 1; i < args.length; i++) {
            reason.append(args[i]);
            if (i < args.length - 1) reason.append(" ");
        }

        // Kick the player
        String kickMessage = plugin.getConfig().getString("messages.player-kicked")
                .replace("{reason}", reason.toString());
        target.kickPlayer(plugin.colorize(kickMessage));

        // Send confirmation message
        String successMessage = plugin.getConfig().getString("messages.kick-success")
                .replace("{player}", target.getName())
                .replace("{reason}", reason.toString());
        sender.sendMessage(plugin.colorize(successMessage));

        return true;
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class KickCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public KickCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("banplugin.kick")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length < 2) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.usage-kick")));
            return true;
        }

        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        // Prevent players from kicking themselves
        if (sender instanceof Player && target.getName().equals(((Player) sender).getName())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        // Staff-Bypass Check
        if (target.hasPermission("banplugin.bypass") || target.isOp()) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.staff-bypass")));
            return true;
        }

        // Combine remaining arguments for kick reason
        StringBuilder reason = new StringBuilder();
        for (int i = 1; i < args.length; i++) {
            reason.append(args[i]);
            if (i < args.length - 1) reason.append(" ");
        }

        // Kick the player
        String kickMessage = plugin.getConfig().getString("messages.player-kicked")
                .replace("{reason}", reason.toString());
        target.kickPlayer(plugin.colorize(kickMessage));

        // Send confirmation message
        String successMessage = plugin.getConfig().getString("messages.kick-success")
                .replace("{player}", target.getName())
                .replace("{reason}", reason.toString());
        sender.sendMessage(plugin.colorize(successMessage));

        return true;
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.OfflinePlayer;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.command.ConsoleCommandSender;
import org.bukkit.configuration.ConfigurationSection;
import org.bukkit.entity.Player;

public class MuteCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public MuteCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        // Skip permission check if sender is console
        if (!(sender instanceof ConsoleCommandSender) && !sender.hasPermission("banplugin.mute")) {
            sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                plugin.getConfig().getString("messages.no-permission", "&cYou don't have permission to use this command!")));
            return true;
        }

        if (args.length == 0) {
            showMuteUsage(sender);
            return true;
        }

        if (args.length < 2) {
            sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                plugin.getConfig().getString("messages.usage-mute")));
            return true;
        }

        String playerName = args[0];

        // Verhindere, dass Spieler sich selbst muten knnen
        if (sender instanceof Player && playerName.equalsIgnoreCase(((Player) sender).getName())) {
            sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                plugin.getConfig().getString("messages.cannot-target-self")));
            return true;
        }

        boolean useIdSystem = plugin.getConfig().getBoolean("settings.use-id-system.mute", true);

        // Combine all arguments after player name for the reason/id
        StringBuilder inputBuilder = new StringBuilder();
        for (int i = 1; i < args.length; i++) {
            inputBuilder.append(args[i]);
            if (i < args.length - 1) {
                inputBuilder.append(" ");
            }
        }
        String input = inputBuilder.toString();

        // Find the mute entry either by ID or reason
        String muteId = null;
        ConfigurationSection mutes = plugin.getConfig().getConfigurationSection("mutes");

        if (useIdSystem) {
            // Using ID system
            if (!mutes.contains(input)) {
                sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    plugin.getConfig().getString("messages.invalid-mute-id")));
                return true;
            }
            muteId = input;
        } else {
            // Using reason system - find the ID by reason
            for (String id : mutes.getKeys(false)) {
                String reason = mutes.getString(id + ".reason");
                if (reason != null && reason.equalsIgnoreCase(input)) {
                    muteId = id;
                    break;
                }
            }

            if (muteId == null) {
                sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    "&cInvalid mute reason! Use TAB to see available reasons."));
                return true;
            }
        }

        OfflinePlayer target = Bukkit.getOfflinePlayer(playerName);

        // Only check for bypass permission if target is online and sender is not console
        if (target.isOnline()) {
            Player onlineTarget = (Player) target;
            if (onlineTarget.hasPermission("banplugin.bypass") || onlineTarget.isOp()) {
                sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    plugin.getConfig().getString("messages.staff-bypass")));
                return true;
            }
        }

        if (plugin.getBansConfig().contains("muted-players." + target.getUniqueId().toString())) {
            sender.sendMessage(ChatColor.translateAlternateColorCodes('&',
                plugin.getConfig().getString("messages.already-muted").replace("{player}", target.getName())));
            return true;
        }

        String reason = plugin.getConfig().getString("mutes." + muteId + ".reason");
        String duration = plugin.getConfig().getString("mutes." + muteId + ".duration");
        long durationMs = parseDuration(duration);
        long expiryTime = System.currentTimeMillis() + durationMs;

        // Add to history
        plugin.addToHistory(
            target.getUniqueId().toString(),
            "MUTE",
            sender.getName(),
            reason,
            durationMs
        );

        // Save mute in config
        String mutePath = "muted-players." + target.getUniqueId().toString();
        plugin.getBansConfig().set(mutePath + ".reason", reason);
        plugin.getBansConfig().set(mutePath + ".by", sender.getName());
        plugin.getBansConfig().set(mutePath + ".time", System.currentTimeMillis());
        plugin.getBansConfig().set(mutePath + ".expires", expiryTime);
        plugin.saveBansConfig();

        // Debug output
        plugin.getLogger().info("Muting player " + target.getName() + " (UUID: " + target.getUniqueId().toString() + ")");
        plugin.getLogger().info("Mute data: reason=" + reason + ", expires=" + expiryTime);

        // Reload the config to ensure it's saved
        plugin.reloadBansConfig();

        String muteMessage = plugin.getConfig().getString("messages.mute-success")
            .replace("{player}", target.getName())
            .replace("{reason}", reason)
            .replace("{duration}", duration);
        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', muteMessage));

        if (target.isOnline()) {
            String playerMessage = plugin.getConfig().getString("messages.player-muted")
                .replace("{reason}", reason)
                .replace("{expires}", duration);
            target.getPlayer().sendMessage(ChatColor.translateAlternateColorCodes('&', playerMessage));
        }

        return true;
    }

    private void showMuteUsage(CommandSender sender) {
        String divider = plugin.getConfig().getString("usage-format.divider", "&7&m--------------------------------");
        boolean useIdSystem = plugin.getConfig().getBoolean("settings.use-id-system.mute", true);

        String header = useIdSystem ?
            plugin.getConfig().getString("usage-format.mute-command.header", "&cUsage: &7/mute <player> <mute-id>") :
            "&cUsage: &7/mute <player> <reason>";

        String listHeader = useIdSystem ?
            plugin.getConfig().getString("usage-format.mute-command.list-header", "&cAvailable Mute IDs:") :
            "&cAvailable Mute Reasons:";

        String format = useIdSystem ?
            plugin.getConfig().getString("usage-format.mute-command.format", "&7ID: &c{id} &7| Reason: &c{reason} &7| Duration: &c{duration}") :
            "&7Reason: &c{reason} &7| Duration: &c{duration}";

        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', divider));
        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', header));
        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', divider));
        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', listHeader));

        ConfigurationSection mutes = plugin.getConfig().getConfigurationSection("mutes");
        if (mutes != null) {
            for (String id : mutes.getKeys(false)) {
                String reason = mutes.getString(id + ".reason");
                String duration = mutes.getString(id + ".duration");
                String line = format
                    .replace("{id}", id)
                    .replace("{reason}", reason)
                    .replace("{duration}", duration);
                sender.sendMessage(ChatColor.translateAlternateColorCodes('&', line));
            }
        }

        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', divider));
    }

    private long parseDuration(String duration) {
        if (duration == null) return 0;

        long multiplier;
        if (duration.endsWith("s")) {
            multiplier = 1000; // Sekunden zu Millisekunden
        } else if (duration.endsWith("m")) {
            multiplier = 1000 * 60; // Minuten zu Millisekunden
        } else if (duration.endsWith("h")) {
            multiplier = 1000 * 60 * 60; // Stunden zu Millisekunden
        } else if (duration.endsWith("d")) {
            multiplier = 1000 * 60 * 60 * 24; // Tage zu Millisekunden
        } else {
            return 0;
        }

        try {
            String numberPart = duration.substring(0, duration.length() - 1);
            return Long.parseLong(numberPart) * multiplier;
        } catch (NumberFormatException e) {
            return 0;
        }
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.OfflinePlayer;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.command.ConsoleCommandSender;
