                String duration = mutes.getString(id + ".duration");
                String line = format
                    .replace("{id}", id)
                    .replace("{reason}", reason)
                    .replace("{duration}", duration);
                sender.sendMessage(ChatColor.translateAlternateColorCodes('&', line));
            }
        }

        sender.sendMessage(ChatColor.translateAlternateColorCodes('&', divider));
    }

    private long parseDuration(String duration) {
        if (duration == null) return 0;

        long multiplier;
        if (duration.endsWith("s")) {
            multiplier = 1000; // Seconds to milliseconds
        } else if (duration.endsWith("m")) {
            multiplier = 1000 * 60; // Minutes to milliseconds
        } else if (duration.endsWith("h")) {
            multiplier = 1000 * 60 * 60; // Hours to milliseconds
        } else if (duration.endsWith("d")) {
            multiplier = 1000 * 60 * 60 * 24; // Days to milliseconds
        } else {
            return 0;
        }

        try {
            String numberPart = duration.substring(0, duration.length() - 1);
            return Long.parseLong(numberPart) * multiplier;
        } catch (NumberFormatException e) {
            return 0;
        }
    }
}

package org.viirless.smartban;

import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerMoveEvent;
import org.bukkit.event.player.PlayerQuitEvent;

public class PlayerFreezeListener implements Listener {
    private final BanPlugin plugin;

    public PlayerFreezeListener(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @EventHandler
    public void onPlayerMove(PlayerMoveEvent event) {
        Player player = event.getPlayer();
        if (plugin.getFrozenPlayers().contains(player.getUniqueId())) {
            // Only cancel if the player actually moved position (not just looked around)
            if (event.getTo().getX() != event.getFrom().getX() ||
                event.getTo().getY() != event.getFrom().getY() ||
                event.getTo().getZ() != event.getFrom().getZ()) {
                event.setCancelled(true);

                player.sendTitle(
                    plugin.colorize("&cYou have been frozen!"),
                    plugin.colorize("&7Contact a staff member in the discord"),
                    10,
                    60,
                    20
                );
            }
        }
    }

    @EventHandler
    public void onPlayerQuit(PlayerQuitEvent event) {
        // Optional: Remove player from frozen set when they leave
        // Uncomment if you want frozen status to reset on logout
        // plugin.getFrozenPlayers().remove(event.getPlayer().getUniqueId());
    }
}

package org.viirless.smartban;

import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
