    }
}

package org.viirless.smartban;

import org.bukkit.ChatColor;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import java.util.Date;

public class PlayerJoinListener implements Listener {

    private final BanPlugin plugin;

    public PlayerJoinListener(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        String uuid = event.getPlayer().getUniqueId().toString();

        // Check if player is banned
        if (!plugin.getBansConfig().contains("banned-players." + uuid)) {
            return; // Player is not banned
        }

        // Get ban details
        long expiryTime = plugin.getBansConfig().getLong("banned-players." + uuid + ".expires");
        String reason = plugin.getBansConfig().getString("banned-players." + uuid + ".reason");

        // Check if ban has expired
        if (System.currentTimeMillis() >= expiryTime) {
            // Ban has expired, remove it
            plugin.getBansConfig().set("banned-players." + uuid, null);
            plugin.saveBansConfig();
            return; // Let player join
        }

        // Ban is still active, kick the player
        String kickMessage = plugin.getConfig().getString("messages.player-banned")
                .replace("{reason}", reason)
                .replace("{expires}", new Date(expiryTime).toString());

        event.getPlayer().kickPlayer(colorize(kickMessage));
    }

    private String colorize(String message) {
        return ChatColor.translateAlternateColorCodes('&', message);
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;

import java.util.Date;

public class PlayerJoinListener implements Listener {

    private final BanPlugin plugin;

    public PlayerJoinListener(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        Player player = event.getPlayer();

        // Check if player is banned
        if (plugin.getBansConfig().contains("banned-players." + player.getUniqueId().toString())) {
            String uuid = event.getPlayer().getUniqueId().toString();
            String banPath = "banned-players." + uuid;

            // Get ban details
            long banTime = plugin.getBansConfig().getLong(banPath + ".time");
            long duration = plugin.getBansConfig().getLong(banPath + ".duration");
            String reason = plugin.getBansConfig().getString(banPath + ".reason");

            // Calculate expiry time (-1 duration means permanent ban)
            if (duration == -1) {
                // Permanent ban
                kickBannedPlayer(event, reason, -1);
                return;
            }

            long expiryTime = banTime + duration;

            // Check if ban has expired
            if (System.currentTimeMillis() >= expiryTime) {
                // Ban has expired, remove it
                plugin.getBansConfig().set(banPath, null);
                plugin.saveBansConfig();
                return; // Let player join
            }

            // Ban is still active, kick the player
            kickBannedPlayer(event, reason, expiryTime);
        }

        // Hide vanished players from the joining player
        if (!player.hasPermission("banplugin.vanish.see")) {
            for (Player onlinePlayer : Bukkit.getOnlinePlayers()) {
                if (plugin.getVanishedPlayers().contains(onlinePlayer.getUniqueId())) {
                    player.hidePlayer(plugin, onlinePlayer);
                }
            }
        }

        // If the joining player is vanished, hide them from others
        if (plugin.getVanishedPlayers().contains(player.getUniqueId())) {
            for (Player onlinePlayer : Bukkit.getOnlinePlayers()) {
                if (!onlinePlayer.hasPermission("banplugin.vanish.see")) {
                    onlinePlayer.hidePlayer(plugin, player);
                }
            }
            player.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.vanish.enabled")));
        }
    }

    private void kickBannedPlayer(PlayerJoinEvent event, String reason, long expiryTime) {
        String kickMessage = plugin.getConfig().getString("messages.player-banned")
                .replace("{reason}", reason)
                .replace("{expires}", expiryTime == -1 ? "Never" : new Date(expiryTime).toString());

        event.getPlayer().kickPlayer(colorize(kickMessage));
    }

    private String colorize(String message) {
        return ChatColor.translateAlternateColorCodes('&', message);
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class UnfreezeCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public UnfreezeCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("banplugin.unfreeze")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        if (args.length < 1) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.unfreeze-usage")));
            return true;
        }

        Player target = Bukkit.getPlayer(args[0]);
        if (target == null) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.player-not-online")));
            return true;
        }

        if (!plugin.getFrozenPlayers().contains(target.getUniqueId())) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.freeze.not-frozen")));
            return true;
        }

        plugin.getFrozenPlayers().remove(target.getUniqueId());
        String message = plugin.getConfig().getString("messages.freeze.unfrozen-by")
                .replace("{player}", target.getName());
        sender.sendMessage(plugin.colorize(message));

        // Clear the freeze title
        target.resetTitle();
        target.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.freeze.unfrozen")));

        return true;
    }
}

package org.viirless.smartban;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

import java.util.List;

public class VanishCommand implements CommandExecutor {
    private final BanPlugin plugin;

    public VanishCommand(BanPlugin plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(plugin.colorize("&cThis command can only be used by players!"));
            return true;
        }

        if (!sender.hasPermission("banplugin.vanish")) {
            sender.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.no-permission")));
            return true;
        }

        Player player = (Player) sender;

        if (plugin.getVanishedPlayers().contains(player.getUniqueId())) {
            // Unvanish the player
            plugin.getVanishedPlayers().remove(player.getUniqueId());

            // Show player to everyone
            for (Player onlinePlayer : Bukkit.getOnlinePlayers()) {
                onlinePlayer.showPlayer(plugin, player);
            }

            player.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.vanish.disabled")));
        } else {
            // Vanish the player
            plugin.getVanishedPlayers().add(player.getUniqueId());

            // Hide player from everyone except those with see permission
            for (Player onlinePlayer : Bukkit.getOnlinePlayers()) {
                if (!onlinePlayer.hasPermission("banplugin.vanish.see")) {
                    onlinePlayer.hidePlayer(plugin, player);
                }
            }

            player.sendMessage(plugin.colorize(plugin.getConfig().getString("messages.vanish.enabled")));
        }

        return true;
    }
}
